import React from 'react';
import tokenService from '../../../services/TokenService';
import queryString from 'query-string';


export default class EditService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            service_type: '1',
            comments: 'wqeqewq',
            day: 'ewqwe',
            best_time_reached: '1',
            date_created: '1',
            price: '1'
        }
    }

    componentWillMount(){
        const serviceId = queryString.parse(this.props.location.search);
        console.log(serviceId)
        fetch(`http://localhost:8000/user/service`, {
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            }
        })
        .then( res => res.json())
        .then(resData => {

            const currentService = resData.services.filter( service => service.id == serviceId.id)[0]
            
            this.setState({ serivce_type: currentService.serivce_type, comments: currentService.comments, day: currentService.day, best_time_reached: currentService.best_time_reached, price: currentService.price
            })
        })
        console.log(this.state)
    }

    handleSubmit = (e)=>{
        const serviceId = queryString.parse(this.props.location.search);
        e.preventDefault();
        fetch(`http://localhost:8000/user/service`, {
            method: 'PATCH',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.service_type, comments: this.state.comments, day: this.state.day, best_time_reached: this.state.best_time_reached, price: this.state.price, id: serviceId.id, date_modified: new Date()})
        })
        this.props.history.push('/user/services')
    }

    render(){
        console.log(this.state)
        return (
            <section>
                <form>
                    <fieldset>
                        <label htmlFor="edit_type">Service type:</label>
                        <input type="text" id="edit_type"></input>

                        <label htmlFor="edit_comments">Comments
                        ?</label>
                        <input type="text" id="edit_comments"></input>

                        <label htmlFor="edit_day">Day</label>
                        <input type="text" id="edit_day"></input>

                        <label htmlFor="edit_time">Time:
                        </label>
                        <input type="text" id="edit_time"></input>

                        <label htmlFor="edit_comments">
                        ?</label>
                        <input type="text" id="edit_comments"></input>
                    </fieldset>
                </form>
            </section>
        )
    }
}
