import React from 'react';



export default class RequestService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            service_type: '',
            day: '',
            best_time_reached: '',
            comments: '',
            price: ''
        }
    }

    handelService = (e)=>{
        this.setState({service_type: e.target.value});
    }

    handleDay = (e)=>{
        this.setState({day: e.target.value});
    }

    handleBestTime = (e)=>{
        this.setState({best_time_reached: e.target.value});
    }

    handleComments = (e)=>{
        this.setState({comments: e.target.value});
    }

    handlePrice = (e)=>{
        this.setState({price: e.target.value});
    }


    handleSubmit = (e)=>{
        e.preventDefault();
        fetch('http://localhost:8000/user/bad3a8a5-2ca5-420d-85f0-8ade0c9b5936carcamo/service', {
            method: 'POST',
            headers: {
                'content-type': 'application/json'
            },
            body: JSON.stringify({service_type: 'Initial, Windows', comments: 'Ok, Thanks', day: 'Tuesday', best_time_reached: '8:00AM', price: '$94.00', user_id: this.props.user})
        })
        .then( res => res.json())
        .then(resData => console.log(resData));
    }


    render(){
        return (
            <section>
                <form onSubmit={this.handleSubmit}>
                    <fieldset>

                        <label htmlFor="req_service_type">service type</label>
                        <input type="text" id="req_service_type"></input>

                        <label htmlFor="req_day">What day?</label>
                        <input type="text" id="req_day"></input>

                        <label htmlFor="req_best_time">What time?</label>
                        <input type="text" id="req_best_time"></input>

                        <label htmlFor="req_comments">Comments?</label>
                        <button type="submit">Go</button>
                    </fieldset>
                </form>
            </section>
        )
    }
}
