import React from 'react';
import AuthService from '../../services/AuthService';
import uuid4 from 'uuid/v4';

export default class Registration extends React.Component{
    constructor(props){
        super(props);
        this.state = {
           user_name: '',
           password: '',
           first_name: '',
           last_name: '',
           email: '' ,
           phone: null
        }
    }

    handleUserName = (e)=>{
        this.setState({user_name: e.target.value});
    }

    handlePassword = (e)=>{
        this.setState({password: e.target.value});
    }

    handleFirstName = (e)=>{
        this.setState({ first_name: e.target.value});
    }

    handleLastName = (e)=>{
        this.setState({ last_name: e.target.value});
    }

    handleEmail = (e)=>{
        this.setState({ email: e.target.value});
    }

    handlePhoneNumber =(e)=>{
        this.setState({ phone_number: e.target.value})
    }




    handleSubmit = (e)=>{
        e.preventDefault();


        let {user_name, password, first_name, last_name, email} = this.state;

        const profile_id = uuid4();

        let newUser = {
            user_name,
            password,
            first_name,
            last_name,
            email,
            phone_number,
            id: profile_id
        }

        console.log(profile_id)
        AuthService.registerUser(newUser).then( res => console.log(res))

    }

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
                <fieldset>
                    <label htmlFor="reg_user_name">Enter a Username</label>
                    <input type="text" id="reg_user_name" onChange={this.handleUserName} value={this.state.user_name}/>

                    <label htmlFor="reg_password">Enter a password</label>
                    <input type="password" id="reg_password" onChange={this.handlePassword} value={this.state.password}/>

                    <label htmlFor="reg_first_name">First Name:</label>
                    <input type="text" id="reg_first_name" onChange={this.handleFirstName} value={this.state.first_name}/>

                    <label htmlFor="reg_last_name">Last Name:</label>
                    <input type="text" id="reg_last_name" onChange={this.handleLastName} value={this.state.last_name}/>

                    <label htmlFor="reg_email">Email:</label>
                    <input type="text" id="reg_email" onChange={this.handleEmail} value={this.state.email}/>

                    <button type="submit">Sign me up</button>
                </fieldset>
            </form>
        )
    }
}
