import React from 'react';
import AuthService from '../../services/AuthService';
import uuid4 from 'uuid/v4';

export default class Registration extends React.Component{
    constructor(props){
        super(props);
        this.state = {
           first_name: '',
           last_name: '',
           email: '' ,
           password: '',
           home_number: null,
           mobile_number: null,
           address: '',
           city: '',
           state_region: '',
           zipcode: null,
           best_days_reached: '',
           best_time_reached: ''
        }
    }

    handleUserName = (e)=>{
        this.setState({user_name: e.target.value});
    }

    handlePassword = (e)=>{
        this.setState({password: e.target.value});
    }

    handleFirstName = (e)=>{
        this.setState({ first_name: e.target.value});
    }

    handleLastName = (e)=>{
        this.setState({ last_name: e.target.value});
    }

    handleEmail = (e)=>{
        this.setState({ email: e.target.value});
    }

    handleHomeNumber =(e)=>{
        this.setState({ home_number: e.target.value})
    }




    handleSubmit = (e)=>{
        e.preventDefault();


        let {first_name, last_name, email, password, home_number, mobile_number, address, city, state_region, zipcode, best_days_reached, best_time_reached} = this.state;

        phone_number = phone_number.split('-').join('');

        const profile_id = uuid4();

        let newUser = {
            _name,
            password,
            first_name,
            last_name,
            email,
            home_number,
            mobile_number,
            id: profile_id + last_name
        }

        
        AuthService.registerUser(newUser).then( res => '')

    }

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
                <fieldset>
                

                    <label htmlFor="reg_first_name">First Name:</label>
                    <input type="text" id="reg_first_name" onChange={this.handleFirstName} value={this.state.first_name}/>

                    <label htmlFor="reg_last_name">Last Name:</label>
                    <input type="text" id="reg_last_name" onChange={this.handleLastName} value={this.state.last_name}/>


                    <label htmlFor="reg_email">Email:</label>
                    <input type="text" id="reg_email" onChange={this.handleEmail} value={this.state.email}/>

                    <label htmlFor="reg_password">Enter a password</label>
                    <input type="password" id="reg_password" onChange={this.handlePassword} value={this.state.password}/>
                    
                    <label htmlFor="reg_home_number">Home Number:</label>
                    <input text="text" id="reg_phone_number" onChange={this.handleHomeNumber} value={this.state.phone_number}></input>

                    <label htmlFor="reg_mobile_number">Mobile Number:</label>
                    <input type="text" id="reg_mobile_number"/> 

                    <label htmlFor="reg_address">Address:</label>
                    <input type="text" id="reg_address"/>

                    <label htmlFor="reg_state">State Region:</label>
                    <input type="text" id="reg_state"/>

                    <label htmlFor="reg_zipcode"></label>
                    <input type="number" id="reg_zipcode"></input>

                    <label htmlFor="reg_best_days">Best Day/ Days to be reached</label>
                    <input type="text" id="reg_best_days"/>

                    <label htmlFor="reg_best_time"></label> 
                    <input type="text" id="reg_best_days"/>


                    <button type="submit">Sign me up</button>
                </fieldset>
            </form>
        )
    }
}
