import React from 'react';
import TokenService from '../../../services/TokenService';
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import TimePicker from 'react-time-picker';

export default class RequestService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            services: [
                {
                    name: 'Windows',
                    price: '20.00'
                },
                {
                    name: 'Refridgerator',
                    price: '20.00'
                },
                {
                    name: 'Walls',
                    price: '30.00'
                }
            ],
            date: new Date(),
            service_type: '',
            time: '',
            comments: '',
            price: '0.00'
        }
    }

    handleService= (e)=>{
        const name = e.target.name
        let newService;
        let newPrice;
        if(e.target.checked){
            if(this.state.service_type.length === undefined){
                this.setState({ service_type: e.target.name})
            } else{
                 
                newService = this.state.service_type + ',' + e.target.name;
            }
            
            newPrice = parseFloat(this.state.price) + parseFloat(e.target.value)
            
        } else{

            const targetName = e.target.name
            const serviceArray = this.state.service_type.split(',');
            const serviceIndex = serviceArray.indexOf(targetName)
            
            serviceArray.splice(serviceIndex, 1)
            
            newService = serviceArray.join(',')
    
            newPrice = parseFloat(this.state.price) - parseFloat(e.target.value)
        }
        this.setState({service_type: newService})
        this.setState({ price: newPrice});
       
    }

    handleDay = (e,date)=>{
        this.setState({date});        
    }

    handleBestTime = (time)=>{
        this.setState({time});
    }

    handleComments = (e)=>{
        this.setState({comments: e.target.value});
    }

    handlePrice = (e)=>{
        this.setState({price: e.target.value});
    }


    handleSubmit = (e)=>{
        e.preventDefault();
        
        fetch(`http://localhost:8000/user/service`, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${TokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.service_type, comments: this.state.comments, day: this.state.date, best_time_reached: this.state.time, price: this.state.price, user_id: this.props.user, date_modified: new Date()})
        })
        .then( res => res.json())
        .then(resData => this.props.history.push('/user/services'));
    }

    
    render(){
        
        return (
            <section>
                
                <form onSubmit={this.handleSubmit}>
                    <div className="time-picker"></div>
                    <fieldset>
                        <header>Make a request</header>
                        
                        {this.state.services.map( service => (<label key={service.name} htmlFor={`req_${service.name}`}><input type="checkbox" id={`req_${service.name}`} name={service.name} value={service.price} onChange={this.handleService}></input>{service.name}</label>))}

                        <label htmlFor="req_days">When?</label>
                        <DatePicker id="req_days" selected={this.state.date} onChange={this.handleDay}/>

                        <label htmlFor="req_time">What time?</label>
                        <TimePicker id="req_time" onChange={this.handleBestTime} value={this.state.time} disableClock={true} />

                        <label htmlFor="req_comments">Comments?</label>
                        <textarea id="req_comments" onChange={this.handleComments} value={this.state.comments}></textarea>

                        <span>${this.state.price}</span> 

                        <button type="submit">Go</button>
                    </fieldset>
                </form>
            </section>
        )
    }
}
