import React from 'react';
import TokenService from '../../services/TokenService';
import {Link} from 'react-router-dom';
import UserService from '../../services/UserService';
import './header.css'


export default class Header extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            screenWidth: window.innerWidth
        }
    }

    renderLogIn = ()=>{
        return (
            <ul id="header-links">
                <Link to="/register" className="Link" onClick={this.closeMenu}>Sign Up</Link>
                <Link to="/login" className="Link" onClick={this.closeMenu}> Log In</Link>
            </ul>
        )
    }

    componentDidMount(){
        this.setState({screenWidth: window.innerWidth})
        window.addEventListener("resize", this.setState({screenWidth: window.innerWidth}))
    }

    componentWillUnmount(){

    }

    handleResize=()=>{
        this.setState
    }

    closeMenu = () =>{
        const headerLinks = document.getElementById("header-links");
        if(headerLinks.style.display === "flex"){
            headerLinks.style.display = "none";
        }
    }

    renderLogOut = ()=>{
        return (
            <ul id="header-links">
                <li><Link to="/user/services" className="Link">services</Link></li>

                <li><Link to="/" onClick={this.handleLogOut} className="Link">Log Out</Link></li>
            </ul>
        )
    }

    handleLogOut = ()=>{
        UserService.clearId();
        TokenService.clearAuthToken();
        this.props.history.push('/')
    }

    handleMenuIcon = () => {
        const headerLinks = document.getElementById('header-links');
    
        if(headerLinks.style.display !== "flex"){
            headerLinks.style.display = "flex"
        } else{
            headerLinks.style.display = "none"
        };    
    }

    handleDeviceWidth = () =>{
        const headerLinks = document.getElementById('header-links')
        if(this.state.screenWidth > 445){
            console.log(this.state.screenWidth)
        }
    }

    render(){
        this.handleDeviceWidth();
        return (
            <header>
                <nav id="header-nav">
                    <div ><Link to="/" id="logo-icon">Julis Cleaning Service Inc.</Link></div>

                    <div id="menu-icon" onClick={this.handleMenuIcon}>
                        <div></div>
                        <div></div>
                        <div></div>
                    </div>

                    {TokenService.hasAuthToken() ? this.renderLogOut() : this.renderLogIn()}
                </nav>
            </header>
        )
    }
}
