import React from 'react';
import TokenService from '../../../services/TokenService';
import {format as formatDate, getTime} from 'date-fns'

export default class ServiceHistory extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            services: [],
            serivce_type: '123',
            comments: '23',
            day: '13',
            best_time_reached: '1',
            date_created: '32',
            price: '2'
        }
    }

    componentDidMount(){
        fetch('http://localhost:8000/user/service', {            
            headers: {
                'authorization': `bearer ${TokenService.getAuthToken()}`
            }
        })
            .then( res => res.json())
            .then( resData => this.setState({services: resData.services}));
    }

    handleUpdateService = (e)=>{
        e.preventDefault()
        fetch(`http://localhost:8000/user/service`, {
            method: 'PATCH',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${TokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.serivce_type, comments: this.state.comments, day: this.state.day, best_time_reached: this.state.best_time_reached, price: this.state.price, id: e.target.id, date_modified: new Date()})
        })
    }

    handleCancelService = (e)=>{
        e.preventDefault();

        
    }
   
    render(){
        console.log(this.state)
        return(
            <section>
                <ul>
                    {this.state.services.map( service => (<li key={service.id}><header>{formatDate(getTime(service.date_created), 'MMM Do YYYY hh:mm:ss A  ')}</header><p>{service.comments}</p><button onClick={this.handleUpdateService} id={service.id}></button></li>))}
                </ul>
            </section>
        )
    }
}
