import React from 'react';
import ApiContext from '../../apiContext/ApiContext';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            first_name: '', 
            last_name: '', 
            best_day_reach: '',
            best_time_reach: '',
            email: '', 
            home_number: '',
            mobile_number: '',
            date_created: '',
            date_modified: '',
            address: '',
            city: '',
            state_region: '',
            zipcode: '',
            message: '',
            verified: ''
        }
    }

    componentDidMount(){
        fetch(`http:localhost:8000/api/user/${this.props.user}`)
            .then( res => !res.ok ? Promise.reject(res): res.json())
                .then( resData)
    }

    static context = ApiContext;

    handleSubmit = (e)=>{
        e.preventDefault();
        return fetch('http://localhost:8000/user/699b1330-6e25-41b6-a707-3095798aa1b1carcamo').then(res => res.json()).then(resData => console.log(resData));
    }
    render(){
        console.log(this.props)
        return (
            <form onSubmit={this.handleSubmit}>
                <fieldset>
                    {this.props.user}
                    <button type="submit">Go</button>
                </fieldset>
            </form>
        )
    }
}
