import React from 'react';
import tokenService from '../../../services/TokenService';
import queryString from 'query-string';


export default class EditService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            service_type: '',
            comments: '',
            day: '',
            best_time_reached: '',
            date_created: '',
            price: ''
        }
    }
    componentWillMount(){
        const serviceId = queryString.parse(this.props.location.search);
        console.log(serviceId)
        fetch(`http://localhost:8000/user/service`, {
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            }
        })
        .then( res => res.json())
        .then(resData => {

            const currentService = resData.services.filter( service => service.id == serviceId.id)[0]
            
            this.setState({ serivce_type: currentService.serivce_type, comments: currentService.comments, day: currentService.day, best_time_reached: currentService.best_time_reached, price: currentService.price
            })
        })
    }

    handleSubmit = (e)=>{
        const serviceId = queryString.parse(this.props.location.search);
        e.preventDefault();
        fetch(`http://localhost:8000/user/service`, {
            method: 'PATCH',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.service_type, comments: this.state.comments, day: this.state.day, best_time_reached: this.state.best_time_reached, price: this.state.price, id: service, date_modified: new Date()})
        })
    }

    render(){
        console.log(this.state)
        return (
            'Hello'
        )
    }
}
