import React from 'react';
import TokenService from '../../../services/TokenService';
import {format as formatDate, getTime} from 'date-fns'

export default class ServiceHistory extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            services: [],
            serivce_type: '',
            comments: '',
            day: '',
            best_time_reached: '',
            date_created: ''
        }
    }

    componentDidMount(){
        fetch('http://localhost:8000/user/service', {            
            headers: {
                'authorization': `bearer ${TokenService.getAuthToken()}`
            }
        })
            .then( res => res.json())
            .then( resData => this.setState({services: resData.services}));
    }

    handleCancel = (e)=>{
        e.preventDefault()
        console.log(this.state.services)
        fetch(`http://localhost:8000/user/service`, {
            method: 'PATCH',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${TokenService.getAuthToken()}`
            },
            body: JSON.stringify(this.state)
        })
    }
   
    render(){
        console.log(this.state)
        return(
            <section>
                <ul>
                    {this.state.services.map( service => (<li key={service.id}><header>{formatDate(getTime(service.date_created), 'MMM Do YYYY hh:mm:ss A  ')}</header><p>{service.comments}</p><button onClick={this.handleCancel} id={service.id}></button></li>))}
                </ul>
            </section>
        )
    }
}
