import React from 'react';
import ApiContext from '../../apiContext/ApiContext';
import UserService from '../../services/UserService';
import RequestService from './request-service/RequestService';
import TokenService from '../../services/TokenService';
import {Route, Link} from 'react-router-dom';
import ServiceHistory from './service-history/ServiceHistory';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            first_name: 'oknfiwe', 
            last_name: '', 
            best_days_reach: '',
            best_time_reach: '',
            email: '', 
            home_number: '',
            mobile_number: '',
            date_created: '',
            date_modified: '',
            address: '',
            city: '',
            state_region: '',
            zipcode: '',
            message: '',
            verified: ''
        }
    }

    componentDidMount(){
        
        if(UserService.getId()){
            
            fetch(`http://localhost:8000/user/`, {
                headers: {
                    'authorization': `bearer ${TokenService.getAuthToken()}`
                }
            })
                .then( res => (!res.ok) ? res.json().then(e => Promise.reject(e)): res.json())
                    .then( resData => {
                        
                        this.setState({

                            first_name: resData.first_name,
                            last_name: resData.last_name,
                            email: resData.email, 
                            home_number: resData.home_number,
                            mobile_number: resData.mobile_number,
                            date_created: resData.date_created,
                            date_modified: resData.date_modified,
                            address: resData.address,
                            city: resData.city,
                            state_region: resData.state_region,
                            zipcode: resData.zipcode,
                            verified: resData.verified                            
                        })
                    
                    });
        } else{
            UserService.clearId();
            TokenService.clearAuthToken();
            this.props.history.push('/login')
        }
    }

    static context = ApiContext;

    
    render(){
        console.log(this.props)
        let servicesLink = this.props.match.url;
        let requestServiceLink = this.props.match.url;
        console.log(servicesLink, requestServiceLink)
        return (
            <section>
                <Link to='/services'>Service details</Link>
                <Link to='/newservice'>Arrange Clean Up</Link>

                <Route exact path={`${servicesLink}services`} component={ServiceHistory}></Route>
                <h2>Hello {this.state.first_name}</h2>
                <Route exact path={`${requestServiceLink}newservice`} render={props => <RequestService {...props} user={UserService.getId()}></RequestService> }></Route>
            </section>
        )
    }
}
