import React from 'react';
import TokenService from '../../../services/TokenService';
import datePicker from 'js-datepicker';


export default class RequestService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            services: [
                {
                    name: 'Windows',
                    price: '20.00'
                },
                {
                    name: 'Refridgerator',
                    price: '20.00'
                },
                {
                    name: 'Walls',
                    price: '30.00'
                }
            ],
            days: [
                {
                    day: 'Sunday'
                },
                {
                    day: 'Monday'    
                },
                {
                    day: 'Tuesday'
                },
                {
                   day: 'Wednesday'
                },
                {
                    day: 'Thursday'
                },
                {
                    day: 'Friday'
                },
                {
                    day: 'Saturday'
                }
            ],
            service_type: '',
            day: '',
            best_time_reached: '',
            comments: '',
            price: '0.00'
        }
    }

    handleService= (e)=>{
        
        let newPrice;
        if(e.target.checked){
            newPrice = parseFloat(this.state.price) + parseFloat(e.target.value)
            this.setState({price: newPrice})
        } else{
            newPrice = parseFloat(this.state.price) - parseFloat(e.target.value)
        }
        this.setState({ price: newPrice})
    }

    handleDay = (e)=>{
        this.setState({day: e.target.value});        
    }

    handleBestTime = (e)=>{
        this.setState({best_time_reached: e.target.value});
    }

    handleComments = (e)=>{
        this.setState({comments: e.target.value});
    }

    handlePrice = (e)=>{
        this.setState({price: e.target.value});
    }


    handleSubmit = (e)=>{
        e.preventDefault();
        
        fetch(`http://localhost:8000/user/service`, {
            method: 'POST',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${TokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.service_type, comments: this.state.comments, day: this.state.day, best_time_reached: this.state.best_time_reached, price: this.state.price, user_id: this.props.user, date_modified: new Date()})
        })
        .then( res => res.json())
        .then(resData => this.props.history.push('/user/services'));
    }

    
    render(){
        const picker = datePicker('.')
        return (
            <section>
                <form onSubmit={this.handleSubmit}>
                    <div className="time-picker"></div>
                    <fieldset>
                        <header>Make a request</header>
                        
                        {this.state.services.map( service => (<label key={service.name} htmlFor={`req_${service.name}`}><input type="checkbox" id={`req_${service.name}`} name={service.name} value={service.price} onChange={this.handleService}></input>{service.name}</label>))}

                        <label htmlFor="req_days">When?</label>
                        <select id="req_days" onChange={this.handleDay} value={this.state.day}>
                            {this.state.days.map( day => (<option key={day.day} value={day.day}>{day.day}</option>))}
                        </select>

                        <label htmlFor="req_time">What time?</label>
                        <input type="text" id="req_time" onChange={this.handleBestTime} value={this.state.best_time_reached}></input>

                        <label htmlFor="req_comments">Comments?</label>
                        <textarea id="req_comments" onChange={this.handleComments} value={this.state.comments}></textarea>

                        <span>${this.state.price}</span> 

                        <button type="submit">Go</button>
                    </fieldset>
                </form>
            </section>
        )
    }
}
