import React from 'react';
import tokenService from '../../../services/TokenService';
import queryString from 'query-string';
import DatePicker from 'react-datepicker';
import TimePicker from 'react-time-picker';
import 'react-datepicker/dist/react-datepicker.css'


export default class EditService extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            services: [
                {
                    name: 'Windows',
                    price: '20.00'
                },
                {
                    name: 'Refridgerator',
                    price: '20.00'
                },
                {
                    name: 'Walls',
                    price: '30.00'
                }
            ],
            service_type: '',
            comments: '',
            time: '',            
            date: new Date(),
            price: '0.00'
        }
    }

    componentWillMount(){
        const serviceId = queryString.parse(this.props.location.search);
        console.log(serviceId)
        fetch(`http://localhost:8000/user/service`, {
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            }
        })
        .then( res => res.json())
        .then(resData => {

            const currentService = resData.services.filter( service => service.id == serviceId.id)[0]
            
            
    
        })
        
    }

    handleService= (e)=>{
        let newService;
        let newPrice;
        if(e.target.checked){
            
            newService = this.state.service_type + ',' + e.target.name;            
            
            newPrice = parseFloat(this.state.price) + parseFloat(e.target.value)
                        
        } else{
            
            const targetName = e.target.name
            const serviceArray = this.state.service_type.split(',');
            const serviceIndex = serviceArray.indexOf(targetName)
            
            serviceArray.splice(serviceIndex, 1)
            if(serviceArray[0] == ""){
                serviceArray.shift()
            }
            
            newService = serviceArray.join(',')
            
            newPrice = parseFloat(this.state.price) - parseFloat(e.target.value)
        }
        console.log(this.state.service_type)
        this.setState({service_type: newService})
        this.setState({ price: newPrice});
       
    }

    handleComments = (e)=>{
        console.log(this.state.service)
        this.setState({ comments: e.target.value})
    }

    handleDay = (date)=>{
        this.setState({date})
    }

    handleTime = (time)=>{
        this.setState({time})
    }

    handlePrice = (e)=>{
        this.setState({ price: e.target.value})
    }

    handleSubmit = (e)=>{
        
        e.preventDefault();
        const serviceId = queryString.parse(this.props.location.search);
        fetch(`http://localhost:8000/user/service`, {
            method: 'PATCH',
            headers: {
                'content-type': 'application/json',
                'authorization': `bearer ${tokenService.getAuthToken()}`
            },
            body: JSON.stringify({service_type: this.state.service_type, comments: this.state.comments, day: this.state.date, best_time_reached: this.state.time, price: this.state.price, id: serviceId.id, date_modified: new Date()})
        })
        this.props.history.push('/user/services')
    }

    render(){
        
        return (
            <section>
                <form onSubmit={this.handleSubmit}>
                    <fieldset>
                        {this.state.services.map( service => (<label key={service.name} htmlFor={`edit_${service.name}`}><input type="checkbox" id={`edit_${service.name}`} name={service.name} value={service.price} onChange={this.handleService}></input>{service.name}</label>))}

                        <label htmlFor="edit_comments">Comments
                        ?</label>
                        <input type="text" id="edit_comments" onChange={this.handleComments} value={this.state.comments}></input>

                        <label htmlFor="edit_day">Day</label>
                        <DatePicker id="edit_days" selected={this.state.date} onChange={this.handleDay}/>

                        <label htmlFor="edit_time">Time:
                        </label>
                        <TimePicker id="edit_time" onChange={this.handleTime} value={this.state.time} disableClock={true} />

                        <span>${this.state.price}</span>
                        <button type="submit">Go</button>
                    </fieldset>
                </form>
            </section>
        )
    }
}
